import React, { useState, useEffect } from 'react';
import './App.css';

const ws = new WebSocket('ws://localhost:8080');

const App = () => {
  const [servers, setServers] = useState([
    { name: 'Server1', weight: 5, connections: 0, responseTime: 100 },
    { name: 'Server2', weight: 1, connections: 0, responseTime: 200 },
    { name: 'Server3', weight: 1, connections: 0, responseTime: 300 }
  ]);
  const [algorithm, setAlgorithm] = useState('RoundRobin');
  const [serverResponse, setServerResponse] = useState('');

  useEffect(() => {
    ws.onmessage = (message) => {
      const { type, payload } = JSON.parse(message.data);
      if (type === 'RESPONSE') {
        setServerResponse(payload.data.message);
        setServers((prevServers) =>
          prevServers.map((server) =>
            server.name === payload.server.name
              ? { ...server, connections: server.connections + 1, responseTime: payload.responseTime }
              : server
          )
        );
      }
      if (type === 'ALGORITHM_SET') {
        console.log(`Algorithm set to ${payload}`);
      }
    };
  }, []);

  const sendRequest = () => {
    ws.send(JSON.stringify({ type: 'REQUEST' }));
  };

  const changeAlgorithm = (algo) => {
    setAlgorithm(algo);
    ws.send(JSON.stringify({ type: 'SET_ALGORITHM', payload: algo }));
  };

  return (

    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">

      <h1 className="text-3xl font-bold mb-4">Load Balancer Simulator</h1>
      <div className="mb-4">
        <label className="mr-2">Select Algorithm:</label>
        <select
          value={algorithm}
          onChange={(e) => changeAlgorithm(e.target.value)}
          className="rounded border-gray-300"
        >
          <option value="RoundRobin">Round Robin</option>
          <option value="WeightedRoundRobin">Weighted Round Robin</option>
          <option value="LeastConnections">Least Connections</option>
          <option value="LeastResponseTime">Least Response Time</option>
          <option value="IPHash">IP Hash</option>
        </select>
      </div>
      <button onClick={sendRequest} className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
        Send Request
      </button>
      <div className="my-4">
        <h2 className="text-xl font-semibold mb-2">Server Response:</h2>
        <p>{serverResponse}</p>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {servers.map((server) => (
          <div key={server.name} className="bg-white rounded shadow p-4">
            <h3 className="text-lg font-semibold">{server.name}</h3>
            <p>Connections: {server.connections}</p>
            <p>Weight: {server.weight}</p>
            <p>Response Time: {server.responseTime}ms</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
